record.field:
if any([rec.move_type not in ['out_invoice', 'out_refund'] for rec in records]):
    raise UserError("This action is only possible for CUSTOMER invoices/credit notes!")

if any([rec.state != 'posted' for rec in records]):
    raise UserError("This action is only possible for POSTED customer invoices/credit notes!")

if any([rec.tsp_state not in ['not_sent', 'rejected_send'] for rec in records]):
    raise UserError("This action is only possible for customer invoices/credit notes with NOT SENT or SUBMIT REJECTED tsp state!")

for rec in records:
    rec.tsp_send()
    env.cr.commit()

if any([rec.move_type not in ['out_invoice', 'out_refund'] for rec in records]):
    raise UserError("This action is only possible for CUSTOMER invoices/credit notes!")

if any([rec.state != 'posted' for rec in records]):
    raise UserError("This action is only possible for POSTED customer invoices/credit notes!")

if any([rec.tsp_state not in ['accepted_send', 'rejected_edit', 'accepted_edit'] for rec in records]):
    raise UserError("This action is only possible for customer invoices/credit notes with SUBMIT ACCEPTED or EDIT tsp state!")

for rec in records:
    rec.tsp_edit()
    env.cr.commit()

if any([rec.move_type not in ['out_invoice', 'out_refund'] for rec in records]):
    raise UserError("This action is only possible for CUSTOMER invoices/credit notes!")

if any([rec.state != 'cancel' for rec in records]):
    raise UserError("This action is only possible for customer invoices/credit notes with CANCELLED state!")

if any([rec.tsp_state not in ['accepted_send', 'accepted_edit', 'rejected_cancel'] for rec in records]):
    raise UserError("This action is only possible for customer invoices/credit notes with SUBMIT ACCEPTED, EDIT ACCEPTED or REVOKE FAILED tsp state!")

for rec in records:
    rec.tsp_cancel()
    env.cr.commit()

if any([rec.move_type not in ['out_invoice', 'out_refund'] for rec in records]):
    raise UserError("This action is only possible for CUSTOMER invoices/credit notes!")

if any([rec.state not in ['posted', 'cancel'] for rec in records]):
    raise UserError("This action is only possible for POSTED customer invoices/credit notes!")

if any([rec.tsp_state not in ['pending_send', 'pending_edit', 'pending_cancel'] for rec in records]):
    raise UserError("This action is only possible for customer invoices/credit notes with PENDING tsp state!")

for rec in records:
    rec.tsp_sync_inquiry()
    env.cr.commit()
